// Pro tip: You can create code slides like this one!
// Perfect for tutorials, documentation, and technical presentations

function createAwesomeSlideshow() {
  const slides = [];

  // Add different types of content
  slides.push({
    type: 'title',
    data: { title: 'My Presentation', author: 'Your Name' }
  });

  slides.push({
    type: 'text', 
    data: { 
      title: 'Key Points',
      content: 'Main message here',
      bullets: ['Point 1', 'Point 2', 'Point 3']
    }
  });

  return slides;
}
