// Creating a new slide is simple!
function addSlide(type, data) {
  slideData.slides.push({ type, data });
  renderSlides();
  selectSlide(slideData.slides.length - 1);
}

// Each slide type has its own renderer
class Slide {
  getHTML() {
    return this[this.type + 'Slide'](this.data);
  }
}

// Modern JavaScript with ES6+ features
const fetchData = async (url) => {
  try {
    const response = await fetch(url);
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error fetching data:', error);
    throw error;
  }
};

// Using with destructuring and template literals
const { users, posts } = await fetchData('/api/content');
console.log(`Found ${users.length} users and ${posts.length} posts`);

