}

.layout {
  display: grid;
  grid-template-columns: 
    minmax(200px, 1fr) 
    minmax(0, 3fr) 
    minmax(200px, 1fr);
  gap: var(--spacing-unit);
  padding: var(--spacing-unit);
}

